// my-info.jsx
import React, { useState, useEffect } from 'react';
import { Spinner, Alert } from 'react-bootstrap';
import { FaLinkedin, FaGithub, FaEnvelope, FaPhone, FaMapMarkerAlt } from 'react-icons/fa';
import { odoo19Api} from './services/odoo19API'; 

function MyInfo() {
    const [employeeData, setEmployeeData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchEmployeeData();
    }, []);

    const fetchEmployeeData = async () => {
        try {
            setLoading(true);
            setError(null);
            
            // Initialize Odoo API with your credentials
            await odoo19Api.authenticate(
                process.env.REACT_APP_ODOO_USER,
                process.env.REACT_APP_ODOO_PASSWORD
            );

            // Get current employee data
            const data = await odoo19Api.getEmployeeData();
            
            if (data) {
                setEmployeeData(data);
            } else {
                // Fallback to static data if no employee found
                setEmployeeData({
                    name: "Ahmad Numan",
                    job_title: "Web Developer",
                    work_email: "ahmadnuman.dev@gmail.com",
                    work_location: "Jordan - Amman",
                    work_phone: "", // Add if available
                    linkedin_profile: "", // Add your LinkedIn
                    github_profile: "", // Add your GitHub
                    image_1920: null,
                    description: ""
                });
            }
        } catch (err) {
            console.error('Error fetching employee data:', err);
            setError('Failed to load profile data');
            
            // Fallback to static data on error
            setEmployeeData({
                name: "Ahmad Numan",
                job_title: "Web Developer",
                work_email: "ahmadnuman.dev@gmail.com",
                work_location: "Jordan - Amman",
                work_phone: "",
                linkedin_profile: "",
                github_profile: "",
                image_1920: null,
                description: ""
            });
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return (
            <div className="text-center py-3">
                <Spinner animation="border" role="status" size="sm">
                    <span className="visually-hidden">Loading...</span>
                </Spinner>
                <p className="mt-2">Loading profile...</p>
            </div>
        );
    }

    if (error && !employeeData) {
        return (
            <Alert variant="warning">
                <Alert.Heading>Connection Issue</Alert.Heading>
                <p>Using static profile data</p>
            </Alert>
        );
    }

    return (
        <div className="my-info-container">
            <h5 className="card-title mb-4">My Information</h5>
            
            {/* Profile Image */}
            {employeeData.image_1920 && (
                <div className="text-center mb-4">
                    <img 
                        src={`data:image/png;base64,${employeeData.image_1920}`}
                        alt={employeeData.name}
                        className="rounded-circle border border-3 border-primary"
                        style={{ 
                            width: '120px', 
                            height: '120px', 
                            objectFit: 'cover' 
                        }}
                    />
                </div>
            )}
            
            {/* Profile Information */}
            <div className="profile-info">
                <p className="card-text">
                    <b>Name:</b> {employeeData.name}
                </p>
                
                <p className="card-text">
                    <b>Profession:</b> {employeeData.job_title}
                </p>
                
                <p className="card-text">
                    <FaEnvelope className="me-2 text-primary" />
                    <b>Email:</b> 
                    <a 
                        href={`mailto:${employeeData.work_email}`}
                        className="text-decoration-none ms-2"
                    >
                        {employeeData.work_email}
                    </a>
                </p>
                
                {employeeData.work_phone && (
                    <p className="card-text">
                        <FaPhone className="me-2 text-primary" />
                        <b>Phone:</b> 
                        <a 
                            href={`tel:${employeeData.work_phone}`}
                            className="text-decoration-none ms-2"
                        >
                            {employeeData.work_phone}
                        </a>
                    </p>
                )}
                
                <p className="card-text">
                    <FaMapMarkerAlt className="me-2 text-primary" />
                    <b>Location:</b> {employeeData.work_location}
                </p>
                
                {/* Social Links */}
                <div className="social-links mt-4 pt-3 border-top">
                    <p className="card-text"><b>Connect with me:</b></p>
                    <div className="d-flex gap-3">
                        {employeeData.linkedin_profile && (
                            <a 
                                href={employeeData.linkedin_profile} 
                                target="_blank" 
                                rel="noopener noreferrer"
                                className="text-decoration-none"
                            >
                                <FaLinkedin className="fs-5 text-primary" />
                            </a>
                        )}
                        
                        {employeeData.github_profile && (
                            <a 
                                href={employeeData.github_profile} 
                                target="_blank" 
                                rel="noopener noreferrer"
                                className="text-decoration-none"
                            >
                                <FaGithub className="fs-5 text-dark" />
                            </a>
                        )}
                    </div>
                </div>
                
                {/* Status Indicator */}
                {!error && employeeData.name !== "Ahmad Numan" && (
                    <div className="mt-3">
                        <small className="text-success">
                            âœ“ Profile data synced from Odoo
                        </small>
                    </div>
                )}
            </div>
        </div>
    );
}

export default MyInfo;
