import { Card } from 'react-bootstrap';
import emailjs from 'emailjs-com';
import { useState } from 'react';
function ContactMe() {

    const [email, setEmail] = useState("");
    const [message, setMessage] = useState("");
    const [status, setStatus] = useState("");

    const sendEmail = (e) => {
        e.preventDefault();

        if (!email || !message) {
        setStatus("Please fill all fields.");
        return;
        }

        emailjs
        .send(
            "service_ksajumj",
            "template_izy1s1u",
            {
            from_email: email,
            message: message,
            },
            "LaV8254PEU71PPvyw"
        )
        .then(
            () => {
            setStatus("Message sent successfully!");
            setEmail("");
            setMessage("");
            },
            () => {
            setStatus("Failed to send message.");
            }
        );
    };

    return (
        <>
            <Card className="text-center mx-5">
                <Card.Body>
                    <h2>Contact Me</h2>
                    <br />
                    <Card.Text>
                    <input type="email" placeholder="Your email" className='form-control' name='email' required onChange={(e) => setEmail(e.target.value)}/>
                    <br />
                    <textarea placeholder="Your message" rows="5" cols="50" className='form-control' name='message' required onChange={(e) => setMessage(e.target.value)}></textarea>
                    <br />
                    <button type="submit" className='btn btn-dark' style={{width:'150px',borderRadius:'10px'}} onClick={sendEmail}>Send to Me</button>
                    </Card.Text>
                </Card.Body>
            </Card>
        </>
    );
}
export default ContactMe;
