import { Card } from 'react-bootstrap';
import emailjs from 'emailjs-com';
import { useState } from 'react';
function ContactMe() {

    const [email, setEmail] = useState("");
    const [message, setMessage] = useState("");
    const [status, setStatus] = useState("");
    const [subject, setSubject] = useState("");
    const [name, setName] = useState("");

    const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm('service_ksajumj', 'template_izy1s1u', form.current, {
        publicKey: 'qiAiovuzK4KnsjKdO',
      })
      .then(
        () => {
          console.log('SUCCESS!');
        },
        (error) => {
          console.log('FAILED...', error.text);
        },
      );
  };

    return (
        <>
            <Card className="text-center mx-5">
                <Card.Body>
                    <h2>Contact Me</h2>
                    <br />
                    <Card.Text>
                    <input type="email" placeholder="Your email" className='form-control' name='from_email' required />
                    <br />
                    <input type="text" placeholder="Your Name" className='form-control' name='from_name' required />
                    <br />
                    <input type='subject' placeholder="Subject" className='form-control' name='subject' required />
                    <br />
                    <textarea placeholder="Your message" rows="5" cols="50" className='form-control' name='message' required ></textarea>
                    <br />
                    <button type="submit" className='btn btn-dark' style={{width:'150px',borderRadius:'10px'}} onClick={sendEmail}>Send to Me</button>
                    
                    </Card.Text>
                </Card.Body>
            </Card>
        </>
    );
}
export default ContactMe;
