import { Card } from 'react-bootstrap';
import emailjs from 'emailjs-com';
import { useState } from 'react';
function ContactMe() {

    const [email, setEmail] = useState("");
    const [message, setMessage] = useState("");
    const [status, setStatus] = useState("");
    const [subject, setSubject] = useState("");

    const sendEmail = (e) => {
        e.preventDefault();

        if (!email || !message || !subject) {
        setStatus("Please fill all fields.");
        return;
        }
        try {
        emailjs
        .send(
            "service_ksajumj",
            "template_izy1s1u",
            {
            from_email: email,
            message: message,
            },
            "qiAiovuzK4KnsjKdO"
        )
        .then(
            () => {
            setStatus("Message sent successfully!");
            setEmail(`${email}`);
            setSubject(`${subject}`);
            setMessage(`${message}`);
            console.log(email, subject, message);
            console.log(status);
            },

            (error) => {
            setStatus("Failed to send the message. Please try again later.\n" + error.message);
            }
        );
        } catch (error) {
            setStatus("An error occurred while sending the message. Please try again later.\n" + error.message);
        }
    };

    return (
        <>
            <Card className="text-center mx-5">
                <Card.Body>
                    <h2>Contact Me</h2>
                    <br />
                    <Card.Text>
                        <input type='subject' placeholder="Subject" className='form-control' name='subject' required/>
                    <br />
                    <input type="email" placeholder="Your email" className='form-control' name='email' required onChange={(e) => setEmail(e.target.value)} value={email} />
                    <br />
                    <textarea placeholder="Your message" rows="5" cols="50" className='form-control' name='message' required onChange={(e) => setMessage(e.target.value)} value={message}></textarea>
                    <br />
                    <button type="submit" className='btn btn-dark' style={{width:'150px',borderRadius:'10px'}} onClick={sendEmail}>Send to Me</button>
                    
                    </Card.Text>
                </Card.Body>
            </Card>
        </>
    );
}
export default ContactMe;
