import { Card } from 'react-bootstrap';
import emailjs from 'emailjs-com';
import { useState } from 'react';
import React, { useRef } from 'react';
function ContactMe() {

    const form = useRef();
   
    const sendEmail = (e) => {
    e.preventDefault();

    emailjs
        .sendForm('service_ksajumj', 'template_izy1s1u', form.current, {
            publicKey: 'LaV8254PEU71PPvyw',
        })
        .then(
            () => {
            console.log('SUCCESS!');
            },
            (error) => {
            console.log('FAILED...', error.text);
            },
        );
    };
    

    return (
            <Card className="text-center mx-5">
                <Card.Body>
                    <h2>Contact Me</h2>
                    <br />
                    <Card.Text>
                    <form ref={form} onSubmit={sendEmail}>  
                    <input type="email" placeholder="Your email" className='form-control' name='email' required />
                    <br />
                    <textarea placeholder="Your message" rows="5" cols="50" className='form-control' name='message' required></textarea>
                    <br />
                    <button type="submit" className='btn btn-dark' style={{width:'150px',borderRadius:'10px'}}>Send to Me</button>
                    </form>
                    </Card.Text>
                </Card.Body>
            </Card>
    );
}
export default ContactMe;
