import { Card } from 'react-bootstrap';
function ContactMe() {

    function handleSubmit(event) {
        event.preventDefault();
        const form = event.target;
        const email = form.email.value;
        const message = form.message.value;
        console.log('Email:', email);
        console.log('Message:', message);
        form.reset();
    }

    return (
        <>
            <Card className="text-center mx-5">
                <Card.Body>
                    <h2>Contact Me</h2>
                    <br />
                    <Card.Text>
                    <input type="email" placeholder="Your email" className='form-control' name='email'/>
                    <br />
                    <textarea placeholder="Your message" rows="5" cols="50" className='form-control' name='message'></textarea>
                    <br />
                    <button type="submit" className='btn btn-dark' style={{width:'150px',borderRadius:'10px'}}>Send to Me</button>
                    </Card.Text>
                </Card.Body>
            </Card>
        </>
    );
}
export default ContactMe;
